mapper.insert:

insert into PNT_ACCOUNT (ID, POINT, INCOME, 
  TOTAL_INCOME, MODIFIED_TIMESTAMP, DAY_INCOME_STAT_DATE
  )
values (#{id,jdbcType=BIGINT}, #{point,jdbcType=DECIMAL}, #{income,jdbcType=DECIMAL}, 
  #{totalIncome,jdbcType=DECIMAL}, #{modifiedTimestamp,jdbcType=BIGINT}, #{dayIncomeStatDate,jdbcType=DATE}
  )

mapper.select:

select 

from PNT_ACCOUNT


    and POINT = #{point,jdbcType=DECIMAL}


    and INCOME = #{income,jdbcType=DECIMAL}


    and TOTAL_INCOME = #{totalIncome,jdbcType=DECIMAL}


    and MODIFIED_TIMESTAMP = #{modifiedTimestamp,jdbcType=BIGINT}


    and DAY_INCOME_STAT_DATE = #{dayIncomeStatDate,jdbcType=DATE}


select count(*) 
from PNT_ACCOUNT
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from PNT_ACCOUNT


    and ID = #{id,jdbcType=BIGINT}


    and POINT = #{point,jdbcType=DECIMAL}


    and INCOME = #{income,jdbcType=DECIMAL}


    and TOTAL_INCOME = #{totalIncome,jdbcType=DECIMAL}


    and MODIFIED_TIMESTAMP = #{modifiedTimestamp,jdbcType=BIGINT}


    and DAY_INCOME_STAT_DATE = #{dayIncomeStatDate,jdbcType=DATE}


select count(1) 
from PNT_ACCOUNT


    and POINT = #{point,jdbcType=DECIMAL}


    and INCOME = #{income,jdbcType=DECIMAL}


    and TOTAL_INCOME = #{totalIncome,jdbcType=DECIMAL}


    and MODIFIED_TIMESTAMP = #{modifiedTimestamp,jdbcType=BIGINT}


    and DAY_INCOME_STAT_DATE = #{dayIncomeStatDate,jdbcType=DATE}

mapper.update:
  	update PNT_ACCOUNT set 
	  	INCOME=#{newIncome,jdbcType=DECIMAL}, 
	  	TOTAL_INCOME=#{newTotalIncome,jdbcType=DECIMAL}, 
	  	MODIFIED_TIMESTAMP=#{newModifiedTimestamp,jdbcType=BIGINT}

	  		,DAY_INCOME_STAT_DATE = #{dayIncomeStatDate,jdbcType=DATE} 

  	where ID=#{id,jdbcType=BIGINT} 
	  	and INCOME=#{oldIncome,jdbcType=DECIMAL} 
	  	and TOTAL_INCOME=#{oldTotalIncome,jdbcType=DECIMAL} 
	  	and MODIFIED_TIMESTAMP=#{oldModifiedTimestamp,jdbcType=BIGINT}
