mapper.insert:

insert into PNT_INCOME_LOG (ID, ACCOUNT_ID, INCOME_LOG_TYPE, 
  INCOME_BEFORE_CHANGED, CHANGED_INCOME, INCOME_AFTER_CHANGED, 
  CHANGED_TITILE, STAT_DATE, MODIFIED_TIMESTAMP
  )
values (#{id,jdbcType=BIGINT}, #{accountId,jdbcType=BIGINT}, #{incomeLogType,jdbcType=TINYINT}, 
  #{incomeBeforeChanged,jdbcType=DECIMAL}, #{changedIncome,jdbcType=DECIMAL}, #{incomeAfterChanged,jdbcType=DECIMAL}, 
  #{changedTitile,jdbcType=VARCHAR}, #{statDate,jdbcType=DATE}, #{modifiedTimestamp,jdbcType=BIGINT}
  )

mapper.select:

select 

from PNT_INCOME_LOG


    and ACCOUNT_ID = #{accountId,jdbcType=BIGINT}


    and INCOME_LOG_TYPE = #{incomeLogType,jdbcType=TINYINT}


    and INCOME_BEFORE_CHANGED = #{incomeBeforeChanged,jdbcType=DECIMAL}


    and CHANGED_INCOME = #{changedIncome,jdbcType=DECIMAL}


    and INCOME_AFTER_CHANGED = #{incomeAfterChanged,jdbcType=DECIMAL}


    and CHANGED_TITILE = #{changedTitile,jdbcType=VARCHAR}


    and STAT_DATE = #{statDate,jdbcType=DATE}


    and MODIFIED_TIMESTAMP = #{modifiedTimestamp,jdbcType=BIGINT}


select count(*) 
from PNT_INCOME_LOG
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from PNT_INCOME_LOG


    and ID = #{id,jdbcType=BIGINT}


    and ACCOUNT_ID = #{accountId,jdbcType=BIGINT}


    and INCOME_LOG_TYPE = #{incomeLogType,jdbcType=TINYINT}


    and INCOME_BEFORE_CHANGED = #{incomeBeforeChanged,jdbcType=DECIMAL}


    and CHANGED_INCOME = #{changedIncome,jdbcType=DECIMAL}


    and INCOME_AFTER_CHANGED = #{incomeAfterChanged,jdbcType=DECIMAL}


    and CHANGED_TITILE = #{changedTitile,jdbcType=VARCHAR}


    and STAT_DATE = #{statDate,jdbcType=DATE}


    and MODIFIED_TIMESTAMP = #{modifiedTimestamp,jdbcType=BIGINT}


select count(1) 
from PNT_INCOME_LOG


    and ACCOUNT_ID = #{accountId,jdbcType=BIGINT}


    and INCOME_LOG_TYPE = #{incomeLogType,jdbcType=TINYINT}


    and INCOME_BEFORE_CHANGED = #{incomeBeforeChanged,jdbcType=DECIMAL}


