select 

from PNT_INCOME_LOG


    and ACCOUNT_ID = #{accountId,jdbcType=BIGINT}


    and INCOME_LOG_TYPE = #{incomeLogType,jdbcType=TINYINT}


    and INCOME_BEFORE_CHANGED = #{incomeBeforeChanged,jdbcType=DECIMAL}


    and CHANGED_INCOME = #{changedIncome,jdbcType=DECIMAL}


    and INCOME_AFTER_CHANGED = #{incomeAfterChanged,jdbcType=DECIMAL}


    and CHANGED_TITILE = #{changedTitile,jdbcType=VARCHAR}


    and CHANGED_DETAIL = #{changedDetail,jdbcType=VARCHAR}


    and STAT_DATE = #{statDate,jdbcType=DATE}


    and MODIFIED_TIMESTAMP = #{modifiedTimestamp,jdbcType=BIGINT}


    and OLD_MODIFIED_TIMESTAMP = #{oldModifiedTimestamp,jdbcType=BIGINT}


select count(*) 
from PNT_INCOME_LOG
where ID = #{id,jdbcType=BIGINT}


