mapper.insert:

insert into PNT_LOG (ID, ACCOUNT_ID, LOG_TYPE_ID, 
  CHANGED_POINTS, CHANGED_TITILE, CHANGED_DETAIL, 
  ORDER_ID, ORDER_DETAIL_ID, CHANGED_TIMESTAMP
  )
values (#{id,jdbcType=BIGINT}, #{accountId,jdbcType=BIGINT}, #{logTypeId,jdbcType=BIGINT}, 
  #{changedPoints,jdbcType=DECIMAL}, #{changedTitile,jdbcType=VARCHAR}, #{changedDetail,jdbcType=VARCHAR}, 
  #{orderId,jdbcType=BIGINT}, #{orderDetailId,jdbcType=BIGINT}, #{changedTimestamp,jdbcType=BIGINT}
  )

mapper.select:

select 

from PNT_LOG


    and ACCOUNT_ID = #{accountId,jdbcType=BIGINT}


    and LOG_TYPE_ID = #{logTypeId,jdbcType=BIGINT}


    and CHANGED_POINTS = #{changedPoints,jdbcType=DECIMAL}


    and CHANGED_TITILE = #{changedTitile,jdbcType=VARCHAR}


    and CHANGED_DETAIL = #{changedDetail,jdbcType=VARCHAR}


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and ORDER_DETAIL_ID = #{orderDetailId,jdbcType=BIGINT}


    and CHANGED_TIMESTAMP = #{changedTimestamp,jdbcType=BIGINT}


select count(*) 
from PNT_LOG
where ID = #{id,jdbcType=BIGINT}


select count(*) 
from PNT_LOG


    and ID = #{id,jdbcType=BIGINT}


    and ACCOUNT_ID = #{accountId,jdbcType=BIGINT}


    and LOG_TYPE_ID = #{logTypeId,jdbcType=BIGINT}


    and CHANGED_POINTS = #{changedPoints,jdbcType=DECIMAL}


    and CHANGED_TITILE = #{changedTitile,jdbcType=VARCHAR}


    and CHANGED_DETAIL = #{changedDetail,jdbcType=VARCHAR}


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and ORDER_DETAIL_ID = #{orderDetailId,jdbcType=BIGINT}


    and CHANGED_TIMESTAMP = #{changedTimestamp,jdbcType=BIGINT}


