mapper.insert:

insert into PNT_POINTS_LOG (ID, ACCOUNT_ID, POINTS_LOG_TYPE, 
  POINTS_BEFORE_CHANGED, CHANGED_POINTS, POINTS_AFTER_CHANGED, 
  CHANGED_TITILE, CHANGED_DETAIL, ORDER_ID, 
  ORDER_DETAIL_ID, MODIFIED_TIMESTAMP, OLD_MODIFIED_TIMESTAMP
  )
values (#{id,jdbcType=BIGINT}, #{accountId,jdbcType=BIGINT}, #{pointsLogType,jdbcType=TINYINT}, 
  #{pointsBeforeChanged,jdbcType=DECIMAL}, #{changedPoints,jdbcType=DECIMAL}, #{pointsAfterChanged,jdbcType=DECIMAL}, 
  #{changedTitile,jdbcType=VARCHAR}, #{changedDetail,jdbcType=VARCHAR}, #{orderId,jdbcType=BIGINT}, 
  #{orderDetailId,jdbcType=BIGINT}, #{modifiedTimestamp,jdbcType=BIGINT}, #{oldModifiedTimestamp,jdbcType=BIGINT}
  )

mapper.select:

select 

from PNT_POINTS_LOG


    and ACCOUNT_ID = #{accountId,jdbcType=BIGINT}


    and POINTS_LOG_TYPE = #{pointsLogType,jdbcType=TINYINT}


    and POINTS_BEFORE_CHANGED = #{pointsBeforeChanged,jdbcType=DECIMAL}


    and CHANGED_POINTS = #{changedPoints,jdbcType=DECIMAL}


    and POINTS_AFTER_CHANGED = #{pointsAfterChanged,jdbcType=DECIMAL}


    and CHANGED_TITILE = #{changedTitile,jdbcType=VARCHAR}


    and CHANGED_DETAIL = #{changedDetail,jdbcType=VARCHAR}


    and ORDER_ID = #{orderId,jdbcType=BIGINT}


    and ORDER_DETAIL_ID = #{orderDetailId,jdbcType=BIGINT}


    and MODIFIED_TIMESTAMP = #{modifiedTimestamp,jdbcType=BIGINT}


    and OLD_MODIFIED_TIMESTAMP = #{oldModifiedTimestamp,jdbcType=BIGINT}


